</head>
<body>

	<div>
		Número: <input type="text" id="pantalla"/>
		<button id="suma">+</button>
		<button id="igual">=</button>
	</div>

	<script type="text/javascript">
		var acc = 0;
		var op;

		$(document).ready(function(){ 
			$("#pantalla").click(function(){
				$(this).val("");
			});
			$("#suma").click(function(){
				acc = parseFloat($("#pantalla").val());
				op = "+";
			});
			$("#igual").click(function(){
				var n = parseFloat($("#pantalla").val());
				if(op === "+"){
