  }

  $(pantalla).trigger("click");
  $(pantalla).val("Gato");
  $(document).find("#suma").trigger("click");
  $(pantalla).trigger("click");
  $(pantalla).val("2");
  $(document).find("#igual").trigger("click");

  if($(pantalla).val() != "Error"){
    return returnWithError(document,content,grade,"La calculadora debería mostrar \"Error\" cuando alguno de los operandos es una cadena de texto en lugar de un número");
  }

  $(pantalla).trigger("click");
  $(pantalla).val("2");
  $(document).find("#suma").trigger("click");
  $(pantalla).trigger("click");
  $(pantalla).val("Perro");
  $(document).find("#igual").trigger("click");

  if($(pantalla).val() != "Error"){
    return returnWithError(document,content,grade,"La calculadora debería mostrar \"Error\" cuando alguno de los operandos es una cadena de texto en lugar de un número");
  }

  grade.score = 10;
  grade.successes.push("Correcto.");

  resetState(document,content);
  return grade;
};

var returnWithError = function(document,content,grade,msg){
  resetState(document,content);
  grade.errors.push(msg);
  return grade;
};

var resetState = function(document,content){
  if((typeof document != "undefined")&&(typeof document.documentElement != "undefined")&&(typeof content != "undefined")){
	  document.open();
	  document.writeln(content);
	  document.close();
  }
};
