var score = function(document){
  var grade = {};
  grade.successes = [];
  grade.errors = [];
  grade.feedback = [];
  grade.score = 0;

  //Existencia de la cabecera <header>.
	if($(document).find("header").length === 0){
	  	grade.errors.push("No añadiste la cabecera <header>.");
	  	return grade;
	}
	if($(document).find("body header").length === 0){
	  	grade.errors.push("La cabecera <header> debe estar dentro de <body>.");
	  	return grade;
	}
	if($(document).find("body header#cabecera").length === 0){
	  	grade.errors.push("La cabecera <header> debe tener el identificador 'cabecera'.");
	  	return grade;
	}
	if($(document).find("body header#cabecera.grande").length === 0){
	  	grade.errors.push("La cabecera <header> debe tener la clase 'grande'.");
	  	return grade;
	}
	if($(document).find("body header#cabecera.grande[title]").length === 0){
	  	grade.errors.push("La cabecera <header> debe tener un atributo 'title'.");
	  	return grade;
	}
	if($(document).find("body header#cabecera.grande[title='Cabecera principal']").length === 0){
	  	grade.errors.push("La cabecera <header> debe tener un atributo 'title' con valor 'Cabecera principal'.");
	  	return grade;
	}
	var header = $(document).find("body header#cabecera.grande[title='Cabecera principal']");
	var headerContent = $(header).html();

	if(headerContent.replace(/ +/g,"").toLowerCase().indexOf("bienvenidoamicalculadora")==-1){
	  grade.errors.push("El contenido de la cabecera <header> debe ser 'Bienvenido a mi calculadora'.");
	  return grade;
	}

  grade.score = 10;
  grade.successes.push("Perfecto.");

  return grade;
};

