	  	return returnWithError(document,content,grade,"No se encontró el <button> con id 'igual'.");
	}
	if($(document).find("div#contador").length === 0){
	  	return returnWithError(document,content,grade,"No se encontró el <div> con id 'contador'.");
	}
	if(parseInt($(document).find("div#contador").html()) != 0){
	  	return returnWithError(document,content,grade,"El contador debería tener un valor inicial de 0.");
	}

  var pantalla = $(document).find("input#pantalla");
  $(pantalla).trigger("click");
  $(pantalla).val(5);
  $(document).find("#suma").trigger("click");
  $(pantalla).trigger("click");
