  if(parseFloat($(pantalla).val()) != 10){
    return returnWithError(document,content,grade,"La función suma no funciona correctamente");
  }

  //Trigger error
  $(pantalla).trigger("click");
  $(pantalla).val("Gato");
  $(document).find("#suma").trigger("click");
