	  return returnWithError(document,content,grade,"El contador debería tener un valor de 1 después de producirse un error.");
	}

	var errorsToTrigger = 5;
	for(var i=0; i<errorsToTrigger; i++){
  	//Trigger error
    $(pantalla).trigger("click");
    $(pantalla).val("Gato");
    $(document).find("#suma").trigger("click");
    $(pantalla).trigger("click");
    $(pantalla).val("Perro");
