  }

  if(parseInt($(document).find("div#contador").html()) != (errorsToTrigger+1)){
	  return returnWithError(document,content,grade,"El contador no cuenta correctamente los errores.");
	}

	if(!$(pantalla).hasClass("error")){
    return returnWithError(document,content,grade,"La pantalla de la calculadora debería tener la clase 'error' al mostrar los mensajes de texto \"Error\".");
