    return returnWithError(document,content,grade,"La pantalla de la calculadora solo debería tener la clase 'error' al mostrar los mensajes de error.");
  }

  grade.score = 10;
  grade.successes.push("Correcto.");

  resetState(document,content);
  return grade;
};

